 class Btn_app extends MY_Controller{
  function __construct(){
      parent::__construct();
      $this->load->model('Btn_app_model','model');
      $this->load->library("Cl");
      if ($this->cl->acl("btn_app")==false) {
        redirect("errors/er403");
      }
  }

 function _rules(){
		$this->form_validation->set_rules('btn_name', 'Name', 'trim|xss_clean|required');
		$this->form_validation->set_rules('btn_url', 'Url', 'trim|xss_clean|required');
		$this->form_validation->set_rules('btn_icon', 'Icon', 'trim|xss_clean');
		$this->form_validation->set_rules('bg_color', 'Color', 'trim|xss_clean');
		$this->form_validation->set_error_delimiters('<span class="text-danger">', '</span>');
  }

 function index(){
    $this->layouts->set_title('Btn_app');
    $this->layouts->view(config_item("cpanel").'content/btn_app/index');
  }

function json() {
    header('Content-Type: application/json');
    echo $this->model->json();
}


