        redirect("errors/er403");
      }
  }

 function _rules(){
		$this->form_validation->set_rules('pengumuman', 'pengumuman', 'trim|xss_clean|required');
		$this->form_validation->set_rules('desc', 'desc', 'trim|xss_clean|required');
		$this->form_validation->set_rules('image', 'image', 'trim|xss_clean');
		$this->form_validation->set_error_delimiters('<span class="text-danger">', '</span>');
  }

 function index(){
    $this->layouts->set_title('Pengumuman');
    $this->layouts->view(config_item("cpanel").'content/pengumuman/index');
  }

function json() {
    header('Content-Type: application/json');
    echo $this->model->json();
}


