 function tambah($status=''){
    if ($status=='aksi') {
        $json = array('success'=>false ,'alert'=>array());
        if ($this->input->is_ajax_request()) {
            $this->_rules();
            if ($this->form_validation->run()) {
                $insert = array(
																'pengumuman' => $this->input->post('pengumuman',TRUE),
																'desc' => $this->input->post('desc',TRUE),
																'image' => $this->input->post('image',TRUE),
																'slug' => slug($this->input->post('pengumuman',TRUE)),
                                'created_by' => sess('id_auth'),
																'created_at' => date('Y-m-d h:i:s'),
															);
                $this->model->get_insert($insert);
                $json['alert']   = 'Berhasil Menyimpan!';
                $json['success'] = true;
            }else{
                foreach ($_POST as $key => $value) {
                  $json['alert'][$key] = form_error($key);
                }
            }
            echo json_encode($json);
        }
    }else{
      $this->layouts->set_title('Pengumuman');
      $data = array('layout_title' => 'Pengumuman',
                      'button'=>'tambah',
                      'aksi' =>site_url(config_item("cpanel").'pengumuman/tambah/aksi'),
											'id_pengumuman' => set_value('id_pengumuman'),
											'pengumuman' => set_value('pengumuman'),
											'desc' => set_value('desc'),
											'image' => set_value('image'),
											);
			 $this->layouts->view(config_item("cpanel").'content/pengumuman/form',array(),$data);
  //MODAL TAMBAH
  //$this->layouts->view(config_item("cpanel").'content/pengumuman/form',array(),$data,false);
     }
  }

 function edit($id,$status=''){
      if ($status=='aksi') {
          $json = array('success'=>false ,'alert'=>array());
